import { InjectionToken, Provider, inject } from '@angular/core';

export const NgpAccordionConfigToken = new InjectionToken<NgpAccordionConfig>(
  'NgpAccordionConfiguration',
);

export interface NgpAccordionConfig {
  /**
   * Determines whether multiple panels can be open simultaneously.
   * @default false
   */
  multiple: boolean;

  /**
   * The orientation of the accordion.
   * @default 'vertical'
   */
  orientation: 'horizontal' | 'vertical';
}

const defaultAccordionConfig: NgpAccordionConfig = {
  multiple: false,
  orientation: 'vertical',
};

/**
 * Provide the default accordion configuration
 * @param config The accordion configuration
 * @returns The provider
 */
export function provideAccordionConfig(config: Partial<NgpAccordionConfig>): Provider[] {
  return [
    {
      provide: NgpAccordionConfigToken,
      useValue: { ...defaultAccordionConfig, ...config },
    },
  ];
}

/**
 * Inject the accordion configuration
 * @returns The global accordion configuration
 */
export function injectAccordionConfig(): NgpAccordionConfig {
  return inject(NgpAccordionConfigToken, { optional: true }) ?? defaultAccordionConfig;
}

import { InjectionToken, Provider, inject } from '@angular/core';

export interface NgpAvatarConfig {
  /**
   * Define a delay before the fallback is shown. This is useful to only show the fallback for those with slower connections.
   * @default 0
   */
  delay: number;
}

export const defaultAvatarConfig: NgpAvatarConfig = {
  delay: 0,
};

export const NgpAvatarConfigToken = new InjectionToken<NgpAvatarConfig>('NgpAvatarConfigToken');

export function provideNgpAvatarConfig(config: Partial<NgpAvatarConfig>): Provider[] {
  return [
    {
      provide: NgpAvatarConfigToken,
      useValue: { ...defaultAvatarConfig, ...config },
    },
  ];
}

export function injectAvatarConfig(): NgpAvatarConfig {
  return inject(NgpAvatarConfigToken, { optional: true }) ?? defaultAvatarConfig;
}

import { Directive, EmbeddedViewRef, TemplateRef, ViewContainerRef, inject } from '@angular/core';
import { injectSelect } from '../select/select.token';

@Directive({
  selector: '[ngpSelectLabel]',
  standalone: true,
})
export class NgpSelectLabelDirective {
  /**
   * Access the view container reference.
   */
  private readonly viewContainerRef = inject(ViewContainerRef);

  /**
   * Access the select directive.
   */
  private readonly select = injectSelect();

  /**
   * Access the template reference.
   */
  private readonly templateRef = inject(TemplateRef);

  /**
   * Store the embedded view reference.
   * @internal
   */
  private embeddedViewRef?: EmbeddedViewRef<NgpSelectLabelContext>;

  /**
   * Asserts the correct type of the context for the template that `NgpSelectLabel` will render.
   *
   * The presence of this method is a signal to the Ivy template type-check compiler that the
   * `NgpSelectLabel` structural directive renders its template with a specific context type.
   */
  static ngTemplateContextGuard(
    dir: NgpSelectLabelDirective,
    ctx: unknown,
  ): ctx is NgpSelectLabelContext {
    return true;
  }

  ngDoCheck(): void {
    this.update();
  }

  /**
   * Update the value visibility.
   * @internal
   */
  private update(): void {
    // if there is no value then we should show the label
    const shouldShowLabel = this.select.value !== undefined;

    // if the label is already showing then we don't need to do anything
    if (shouldShowLabel && this.embeddedViewRef) {
      // if the value has changed then update the value and run change detection
      if (this.embeddedViewRef.context.$implicit !== this.select.value) {
        this.embeddedViewRef.context.$implicit = this.select.value!;
        this.embeddedViewRef.detectChanges();
      }

      return;
    }

    // if the label is not showing then we need to create it
    if (shouldShowLabel && !this.embeddedViewRef) {
      this.embeddedViewRef = this.viewContainerRef.createEmbeddedView(this.templateRef, {
        $implicit: this.select.value,
      });

      this.embeddedViewRef.detectChanges();
      return;
    }

    // if the label is showing then we need to remove it
    if (!shouldShowLabel && this.embeddedViewRef) {
      this.embeddedViewRef.destroy();
      this.embeddedViewRef = undefined;
      return;
    }
  }
}

export interface NgpSelectLabelContext {
  $implicit: string;
}

import { InjectionToken, Provider, inject } from '@angular/core';

export const NgpTabsetConfigToken = new InjectionToken<NgpTabsetConfig>('NgpTabsetConfiguration');

export interface NgpTabsetConfig {
  /**
   * The orientation of the tabset
   * @default 'horizontal'
   */
  orientation: 'horizontal' | 'vertical';

  /**
   * Whether tabs should activate on focus
   * @default true
   */
  activateOnFocus: boolean;

  /**
   * Whether focus should wrap within the tab list when using the keyboard.
   * @default true
   */
  wrap: boolean;
}

const defaultTabsetConfig: NgpTabsetConfig = {
  orientation: 'horizontal',
  activateOnFocus: true,
  wrap: true,
};

/**
 * Provide the default tabset configuration
 * @param config The tabset configuration
 * @returns The provider
 */
export function provideTabsetConfig(config: Partial<NgpTabsetConfig>): Provider[] {
  return [
    {
      provide: NgpTabsetConfigToken,
      useValue: { ...defaultTabsetConfig, ...config },
    },
  ];
}

/**
 * Inject the tabset configuration
 * @returns The global tabset configuration
 */
export function injectTabsetConfig(): NgpTabsetConfig {
  return inject(NgpTabsetConfigToken, { optional: true }) ?? defaultTabsetConfig;
}

import { InjectionToken, Provider, inject } from '@angular/core';
import { Placement } from '@floating-ui/dom';

export const NgpTooltipConfigToken = new InjectionToken<NgpTooltipConfig>('NgpTooltipConfig');

export interface NgpTooltipConfig {
  /**
   * Define the offset of the tooltip relative to the trigger.
   * @default 4
   */
  offset: number;

  /**
   * Define the placement of the tooltip relative to the trigger.
   * @default 'top'
   */
  placement: Placement;

  /**
   * Define the delay before the tooltip is shown.
   * @default 0
   */
  showDelay: number;

  /**
   * Define the delay before the tooltip is hidden.
   * @default 0
   */
  hideDelay: number;

  /**
   * Define whether the tooltip should flip when there is not enough space for the tooltip.
   * @default true
   */
  flip: boolean;

  /**
   * Define the container in to which the tooltip should be attached.
   * @default document.body
   */
  container: HTMLElement;
}

const defaultTooltipConfig: NgpTooltipConfig = {
  offset: 4,
  placement: 'top',
  showDelay: 0,
  hideDelay: 0,
  flip: true,
  container: document.body,
};

/**
 * Provide the default Tooltip configuration
 * @param config The Tooltip configuration
 * @returns The provider
 */
export function provideTooltipConfig(config: Partial<NgpTooltipConfig>): Provider[] {
  return [
    {
      provide: NgpTooltipConfigToken,
      useValue: { ...defaultTooltipConfig, ...config },
    },
  ];
}

/**
 * Inject the Tooltip configuration
 * @returns The global Tooltip configuration
 */
export function injectTooltipConfig(): NgpTooltipConfig {
  return inject(NgpTooltipConfigToken, { optional: true }) ?? defaultTooltipConfig;
}

import { Observable } from 'rxjs';

/**
 * A simple helper function to create a resize observer as an RxJS Observable.
 */
export function fromResizeEvent(element: HTMLElement): Observable<ResizeEvent> {
  return new Observable(observer => {
    const resizeObserver = new ResizeObserver(entries => {
      // if there are no entries, ignore the event
      if (!entries.length) {
        return;
      }

      // otherwise, take the first entry and emit the dimensions
      const entry = entries[0];

      if ('borderBoxSize' in entry) {
        const borderSizeEntry = entry['borderBoxSize'];
        // this may be different across browsers so normalize it
        const borderSize = Array.isArray(borderSizeEntry) ? borderSizeEntry[0] : borderSizeEntry;

        observer.next({ width: borderSize['inlineSize'], height: borderSize['blockSize'] });
      } else {
        // fallback for browsers that don't support borderBoxSize
        observer.next({
          width: element.offsetWidth,
          height: element.offsetHeight,
        });
      }
    });

    resizeObserver.observe(element);

    return () => resizeObserver.disconnect();
  });
}

export interface ResizeEvent {
  width: number;
  height: number;
}

