import {
  AfterViewInit,
  ChangeDetectorRef,
  Directive,
  ElementRef,
  HostBinding,
  inject,
} from '@angular/core';
import { NgpAccordionPanelToken } from '../accordion-panel/accordion-panel.token';
import { NgpAccordionStateDirective } from '../common/accordion-state.directive';

@Directive({
  selector: '[ngpAccordionContent]',
  standalone: true,
  host: {
    role: 'region',
    '[id]': 'id',
    '[attr.aria-labelledby]': 'labelledby',
  },
  hostDirectives: [NgpAccordionStateDirective],
})
export class NgpAccordionContentDirective implements AfterViewInit {
  /**
   * Access the element ref.
   */
  private readonly elementRef = inject<ElementRef<HTMLElement>>(ElementRef);

  /**
   * Access the change detector ref.
   */
  private readonly changeDetectorRef = inject(ChangeDetectorRef);

  /**
   * Access the panel the content belongs to.
   */
  private readonly panel = inject(NgpAccordionPanelToken);

  /**
   * Derive the id of the content.
   * @internal
   */
  readonly id = `${this.panel.id}-content`;

  /**
   * Derive the id of the trigger.
   * @internal
   */
  readonly labelledby = `${this.panel.id}-trigger`;

  /**
   * Define the width of the content as a CSS variable so it can be used in animations.
   * @internal
   */
  @HostBinding('style.--ngp-accordion-content-width.px')
  protected width = this.elementRef.nativeElement.scrollWidth;

  /**
   * Define the height of the content as a CSS variable so it can be used in animations.
   * @internal
   */
  @HostBinding('style.--ngp-accordion-content-height.px')
  protected height = this.elementRef.nativeElement.scrollHeight;

  ngAfterViewInit(): void {
    this.updateContentSize();
  }

  /**
   * Update the size of the content.
   */
  private updateContentSize(): void {
    this.width = this.elementRef.nativeElement.scrollWidth;
    this.height = this.elementRef.nativeElement.scrollHeight;
    this.changeDetectorRef.detectChanges();
  }
}

import { Directive, HostBinding, Input, booleanAttribute, signal } from '@angular/core';
import { injectAccordionConfig } from '../config/accordion.config';
import { NgpAccordionToken } from './accordion.token';

/**
 * The root accordion directive that all parts should be placed within.
 */
@Directive({
  selector: '[ngpAccordion]',
  standalone: true,
  providers: [{ provide: NgpAccordionToken, useExisting: NgpAccordionDirective }],
})
export class NgpAccordionDirective {
  /**
   * Access the global accordion configuration.
   */
  private readonly config = injectAccordionConfig();

  /**
   * Determines whether multiple panels can be open simultaneously.
   * @default false
   */
  @Input({ alias: 'ngpAccordionMultiple', transform: booleanAttribute }) multiple: boolean =
    this.config.multiple;

  /**
   * The orientation of the accordion.
   * @default 'vertical'
   */
  @HostBinding('attr.data-orientation')
  @Input({ alias: 'ngpAccordionOrientation' })
  orientation: 'horizontal' | 'vertical' = this.config.orientation;

  /**
   * Determines whether the accordion should be disabled.
   * @default false
   */
  @Input({ alias: 'ngpAccordionDisabled', transform: booleanAttribute }) disabled = false;

  /**
   * Store the currently expanded panel(s).
   * @internal
   */
  readonly expanded = signal<string[]>([]);

  /**
   * Expand a panel.
   * @param id The id of the panel to expand.
   */
  expand(id: string): void {
    this.expanded.set(this.multiple ? [...this.expanded(), id] : [id]);
  }

  /**
   * Collapse a panel.
   * @param id The id of the panel to collapse.
   */
  collapse(id: string): void {
    this.expanded.set(this.expanded().filter(expandedId => expandedId !== id));
  }

  /**
   * Toggle a panel.
   * @param id The id of the panel to toggle.
   */
  toggle(id: string): void {
    if (this.expanded().includes(id)) {
      this.collapse(id);
    } else {
      this.expand(id);
    }
  }

  /**
   * Collapse all panels.
   */
  collapseAll(): void {
    this.expanded.set([]);
  }
}

import { Directive } from '@angular/core';
import { NgpAccordionStateDirective } from '../common/accordion-state.directive';

@Directive({
  selector: '[ngpAccordionHeader]',
  standalone: true,
  hostDirectives: [NgpAccordionStateDirective],
})
export class NgpAccordionHeaderDirective {}

import { Directive, EventEmitter, Input, Output, booleanAttribute, computed } from '@angular/core';
import { injectAccordion } from '../accordion/accordion.token';
import { NgpAccordionStateDirective } from '../common/accordion-state.directive';
import { NgpAccordionPanelToken } from './accordion-panel.token';

/**
 * A unique id for the panel.
 */
let uniqueId = 0;

@Directive({
  selector: '[ngpAccordionPanel]',
  standalone: true,
  providers: [{ provide: NgpAccordionPanelToken, useExisting: NgpAccordionPanelDirective }],
  hostDirectives: [NgpAccordionStateDirective],
})
export class NgpAccordionPanelDirective {
  /**
   * Access the accordion the panel belongs to.
   */
  private readonly accordion = injectAccordion();

  /**
   * Determines whether the panel should be expanded.
   * @default false
   */
  @Input({ alias: 'ngpAccordionPanelExpanded', transform: booleanAttribute }) expanded = false;

  /**
   * Determines whether the panel should be disabled.
   * @default false
   */
  @Input({ alias: 'ngpAccordionPanelDisabled', transform: booleanAttribute }) disabled = false;

  /**
   * Event emitted when the panel is expanded.
   */
  @Output('ngpAccordionPanelExpandedChange') readonly expandedChange = new EventEmitter<boolean>();

  /**
   * The unique id of the panel.
   * @internal
   */
  readonly id = `ngp-accordion-panel-${uniqueId++}`;

  /**
   * Determine if this panel is expanded.
   * @internal
   */
  readonly isExpanded = computed(() => this.accordion.expanded().includes(this.id));

  /**
   * Toggle the expanded state of the panel.
   */
  toggle(): void {
    // If the accordion or panel is disabled, do nothing.
    if (this.accordion.disabled || this.disabled) {
      return;
    }

    if (this.isExpanded()) {
      this.accordion.collapse(this.id);
    } else {
      this.accordion.expand(this.id);
    }

    this.expandedChange.emit(this.isExpanded());
  }
}

import { Directive, computed } from '@angular/core';
import { injectAccordionPanel } from '../accordion-panel/accordion-panel.token';
import { injectAccordion } from '../accordion/accordion.token';

@Directive({
  standalone: true,
  host: {
    '[attr.data-state]': 'state()',
    '[attr.data-disabled]': 'isDisabled',
    '[attr.data-orientation]': 'orientation',
  },
})
export class NgpAccordionStateDirective {
  /**
   * Access the accordion the trigger belongs to.
   */
  private readonly accordion = injectAccordion();

  /**
   * Access the panel the trigger belongs to.
   */
  private readonly panel = injectAccordionPanel();

  /**
   * Determine the expanded state of the panel.
   * @internal
   */
  readonly state = computed(() => (this.panel.isExpanded() ? 'open' : 'closed'));

  /**
   * Determine the disabled state of the panel.
   * @internal
   */
  get isDisabled(): boolean {
    return this.panel.disabled || this.accordion.disabled;
  }

  /**
   * Determine the orientation of the accordion.
   * @internal
   */
  get orientation(): 'horizontal' | 'vertical' {
    return this.accordion.orientation;
  }
}

import { Directive, HostListener, inject } from '@angular/core';
import { NgpAccordionPanelToken } from '../accordion-panel/accordion-panel.token';
import { NgpAccordionStateDirective } from '../common/accordion-state.directive';

@Directive({
  selector: '[ngpAccordionTrigger]',
  standalone: true,
  hostDirectives: [NgpAccordionStateDirective],
  host: {
    '[id]': 'triggerId',
    '[attr.aria-expanded]': 'panel.isExpanded()',
    '[attr.aria-controls]': 'contentId',
  },
})
export class NgpAccordionTriggerDirective {
  /**
   * Access the panel the trigger belongs to.
   */
  protected readonly panel = inject(NgpAccordionPanelToken);

  /**
   * Derive the id of the trigger.
   */
  protected get triggerId(): string {
    return `${this.panel.id}-trigger`;
  }

  /**
   * Get the id of the panel content.
   * @internal
   */
  protected readonly contentId = `${this.panel.id}-content`;

  /**
   * Toggle the expanded state of the panel.
   */
  @HostListener('click')
  toggle(): void {
    this.panel.toggle();
  }
}

import { Directive } from '@angular/core';
import { NgpAvatarToken } from './avatar.token';

@Directive({
  selector: '[ngpAvatar]',
  standalone: true,
  providers: [{ provide: NgpAvatarToken, useExisting: NgpAvatarDirective }],
})
export class NgpAvatarDirective {
  /**
   * Store the current state of the avatar.
   * @internal
   */
  state: NgpAvatarState = NgpAvatarState.Idle;

  /**
   * Set the avatar state.
   * @param state The state to set.
   * @internal
   */
  setState(state: NgpAvatarState): void {
    this.state = state;
  }
}

export enum NgpAvatarState {
  Idle,
  Loading,
  Loaded,
  Error,
}

import { Directive, Input, OnDestroy, OnInit, numberAttribute } from '@angular/core';
import { NgpAvatarState } from '../avatar/avatar.directive';
import { injectAvatar } from '../avatar/avatar.token';
import { injectAvatarConfig } from '../config/avatar.config';

@Directive({
  selector: '[ngpAvatarFallback]',
  standalone: true,
  host: {
    '[style.display]': 'visible ? null : "none"',
  },
})
export class NgpAvatarFallbackDirective implements OnInit, OnDestroy {
  /**
   * Access the avatar
   */
  private readonly avatar = injectAvatar();

  /**
   * Access the global configuration.
   */
  private readonly config = injectAvatarConfig();

  /**
   * Define a delay before the fallback is shown. This is useful to only show the fallback for those with slower connections.
   * @default 0
   */
  @Input({ alias: 'ngpAvatarFallbackDelay', transform: numberAttribute }) delay: number =
    this.config.delay;

  /**
   * Determine if this element should be hidden.
   */
  protected get visible(): boolean {
    // we need to check if the element can render and if the avatar is not in a loaded state
    return this.delayElapsed && this.avatar.state !== NgpAvatarState.Loaded;
  }

  /**
   * Determine the delay has elapsed, and we can show the fallback.
   */
  private delayElapsed: boolean = false;

  /**
   * Store the timeout id.
   */
  private timeoutId: number | null = null;

  ngOnInit(): void {
    this.timeoutId = window.setTimeout(() => (this.delayElapsed = true), this.delay);
  }

  ngOnDestroy(): void {
    if (this.timeoutId) {
      window.clearTimeout(this.timeoutId);
    }
  }
}

import { Directive, ElementRef, HostListener, OnInit, inject } from '@angular/core';
import { NgpAvatarState } from '../avatar/avatar.directive';
import { injectAvatar } from '../avatar/avatar.token';

@Directive({
  selector: 'img[ngpAvatarImage]',
  standalone: true,
})
export class NgpAvatarImageDirective implements OnInit {
  /**
   * Access the avatar
   */
  private readonly avatar = injectAvatar();

  /**
   * Access the image element ref.
   */
  private readonly elementRef = inject<ElementRef<HTMLImageElement>>(ElementRef);

  ngOnInit(): void {
    // mark the avatar as loading
    this.avatar.setState(NgpAvatarState.Loading);

    // if there is no src, we can report this as an error
    if (!this.elementRef.nativeElement.src) {
      this.avatar.setState(NgpAvatarState.Error);
    }

    // if the image has already loaded, we can report this to the avatar
    if (this.elementRef.nativeElement.complete) {
      this.avatar.setState(NgpAvatarState.Loaded);
    }
  }

  @HostListener('load')
  protected onLoad(): void {
    this.avatar.setState(NgpAvatarState.Loaded);
  }

  @HostListener('error')
  protected onError(): void {
    this.avatar.setState(NgpAvatarState.Error);
  }
}

import {
  Directive,
  EventEmitter,
  HostListener,
  Input,
  Output,
  booleanAttribute,
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { NgpCheckboxToken } from './checkbox.token';

@Directive({
  selector: 'button[ngpCheckbox]',
  standalone: true,
  providers: [
    { provide: NgpCheckboxToken, useExisting: NgpCheckboxDirective },
    { provide: NG_VALUE_ACCESSOR, useExisting: NgpCheckboxDirective, multi: true },
  ],
  host: {
    // '[attr.data-state]': 'checked ? "checked" : "unchecked"',
    type: 'button',
    role: 'checkbox',
    '[disabled]': 'disabled',
    '[attr.aria-checked]': 'indeterminate ? "mixed" : checked',
    '[attr.data-disabled]': 'disabled ? "" : null',
    '[attr.data-state]': 'state',
  },
})
export class NgpCheckboxDirective implements ControlValueAccessor {
  /**
   * Defines whether the checkbox is checked.
   */
  @Input({ alias: 'ngpCheckboxChecked', transform: booleanAttribute }) checked: boolean = false;

  /**
   * Defines whether the checkbox is indeterminate.
   */
  @Input({ alias: 'ngpCheckboxIndeterminate', transform: booleanAttribute })
  indeterminate: boolean = false;

  /**
   * Defines whether the checkbox is disabled.
   */
  @Input({ alias: 'ngpCheckboxDisabled', transform: booleanAttribute }) disabled: boolean = false;

  /**
   * Event emitted when the checkbox checked state changes.
   */
  @Output('ngpCheckboxCheckedChange') readonly checkedChange = new EventEmitter<boolean>();

  /**
   * Event emitted when the indeterminate state changes.
   */
  @Output('ngpCheckboxIndeterminateChange') readonly indeterminateChange =
    new EventEmitter<boolean>();

  /**
   * Determine the state
   */
  protected get state(): 'checked' | 'unchecked' | 'indeterminate' {
    if (this.indeterminate) {
      return 'indeterminate';
    }
    return this.checked ? 'checked' : 'unchecked';
  }

  /**
   * Store the callback function that should be called when the checkbox checked state changes.
   * @internal
   */
  private onChange?: (checked: boolean) => void;

  /**
   * Store the callback function that should be called when the checkbox is blurred.
   * @internal
   */
  private onTouched?: () => void;

  @HostListener('keydown', ['$event'])
  onKeydown(event: KeyboardEvent): void {
    // According to WAI ARIA, Checkboxes don't activate on enter keypress
    if (event.key === 'Enter') {
      event.preventDefault();
    }
  }

  @HostListener('click')
  onClick(): void {
    this.checked = this.indeterminate ? true : !this.checked;
    this.checkedChange.emit(this.checked);
    this.onChange?.(this.checked);

    // if the checkbox was indeterminate, it isn't anymore
    if (this.indeterminate) {
      this.indeterminate = false;
      this.indeterminateChange.emit(this.indeterminate);
    }
  }

  @HostListener('blur')
  onBlur(): void {
    this.onTouched?.();
  }

  /**
   * Sets the checked state of the checkbox.
   * @param checked The checked state of the checkbox.
   * @internal
   */
  writeValue(checked: boolean): void {
    this.checked = checked;
  }

  /**
   * Registers a callback function that should be called when the checkbox checked state changes.
   * @param fn The callback function.
   * @internal
   */
  registerOnChange(fn: (checked: boolean) => void): void {
    this.onChange = fn;
  }

  /**
   * Registers a callback function that should be called when the checkbox is blurred.
   * @param fn The callback function.
   * @internal
   */
  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  /**
   * Sets the disabled state of the checkbox.
   * @param isDisabled The disabled state of the checkbox.
   * @internal
   */
  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }
}

import { Directive } from '@angular/core';
import { injectCheckbox } from '../checkbox/checkbox.token';

@Directive({
  selector: '[ngpCheckboxIndicator]',
  standalone: true,
  host: {
    '[style.pointer-events]': '"none"',
    '[attr.data-state]': 'checkbox.state',
    '[attr.data-disabled]': 'checkbox.disabled ? "" : null',
  },
})
export class NgpCheckboxIndicatorDirective {
  /**
   * Access the checkbox that the indicator belongs to.
   */
  protected readonly checkbox = injectCheckbox();
}

import { Directive, ElementRef, OnDestroy, OnInit, inject } from '@angular/core';
import { Placement } from '@floating-ui/dom';
import { injectOverlayTrigger } from '../overlay-trigger/overlay-trigger.token';
import { NgpOverlayArrowToken } from './overlay-arrow.token';

@Directive({
  selector: '[ngpOverlayArrow]',
  standalone: true,
  providers: [{ provide: NgpOverlayArrowToken, useExisting: NgpOverlayArrowDirective }],
})
export class NgpOverlayArrowDirective implements OnInit, OnDestroy {
  /**
   * Access the arrow element
   */
  readonly elementRef = inject(ElementRef<HTMLElement>);

  /**
   * Access the overlay trigger
   */
  private readonly overlayTrigger = injectOverlayTrigger();

  /**
   * Register the arrow on init
   * @internal
   */
  ngOnInit(): void {
    this.overlayTrigger.registerArrow(this);
  }

  /**
   * Unregister the arrow on destroy
   * @internal
   */
  ngOnDestroy(): void {
    this.overlayTrigger.unregisterArrow();
  }

  /**
   * Define the position of the arrow.
   */
  setPosition(placement: Placement, arrowX?: number, arrowY?: number): void {
    const staticSide = {
      top: 'bottom',
      right: 'left',
      bottom: 'top',
      left: 'right',
    }[placement.split('-')[0]] as string;

    Object.assign(this.elementRef.nativeElement.style, {
      left: arrowX != null ? `${arrowX}px` : '',
      top: arrowY != null ? `${arrowY}px` : '',
      right: '',
      bottom: '',
      [staticSide]: `-${this.elementRef.nativeElement.offsetWidth / 2}px`,
    });
  }
}

import { Directive, ElementRef, OnDestroy, OnInit, inject } from '@angular/core';
import { injectOverlayTrigger } from '../overlay-trigger/overlay-trigger.token';
import { NgpOverlayToken } from './overlay.token';

@Directive({
  selector: '[ngpOverlay]',
  standalone: true,
  providers: [{ provide: NgpOverlayToken, useExisting: NgpOverlayDirective }],
})
export class NgpOverlayDirective implements OnInit, OnDestroy {
  /**
   * Access the overlay element
   */
  private readonly elementRef = inject(ElementRef<HTMLElement>);

  /**
   * Access the overlay trigger
   */
  private readonly overlayTrigger = injectOverlayTrigger();

  /**
   * Register the overlay on init
   */
  ngOnInit(): void {
    this.overlayTrigger.registerOverlay(this);
  }

  /**
   * Unregister the overlay on destroy
   */
  ngOnDestroy(): void {
    this.overlayTrigger.unregisterOverlay();
  }

  /**
   * Set the position of the overlay
   * @param x The x position
   * @param y The y position
   * @internal
   */
  setPosition(x?: number, y?: number): void {
    Object.assign(this.elementRef.nativeElement.style, {
      left: `${x}px`,
      top: `${y}px`,
    });
  }
}

import { DomPortalOutlet, TemplatePortal } from '@angular/cdk/portal';
import {
  ApplicationRef,
  ComponentFactoryResolver,
  Directive,
  ElementRef,
  EmbeddedViewRef,
  Injector,
  Input,
  StaticProvider,
  TemplateRef,
  ViewContainerRef,
  booleanAttribute,
  inject,
  numberAttribute,
} from '@angular/core';
import {
  Middleware,
  Placement,
  arrow,
  autoUpdate,
  computePosition,
  flip,
  offset,
  shift,
} from '@floating-ui/dom';
import type { NgpOverlayArrowDirective } from '../overlay-arrow/overlay-arrow.directive';
import type { NgpOverlayDirective } from '../overlay/overlay.directive';
import { NgpOverlayTriggerToken } from './overlay-trigger.token';

@Directive({
  selector: '[ngpOverlayTrigger]',
  standalone: true,
  exportAs: 'ngpOverlayTrigger',
  host: {
    '[attr.data-state]': 'state',
  },
})
export class NgpOverlayTriggerDirective {
  /**
   * Access the application ref
   */
  private readonly appRef = inject(ApplicationRef);

  /**
   * Access the component factory resolver
   */
  private readonly componentFactoryResolver = inject(ComponentFactoryResolver);

  /**
   * Access the injector
   */
  private readonly injector = inject(Injector);

  /**
   * Access the trigger element
   */
  private readonly trigger = inject(ElementRef<HTMLElement>);

  /**
   * Access the view container
   */
  private readonly viewContainer = inject(ViewContainerRef);

  /**
   * Define the overlay to display when the trigger is activated.
   */
  @Input({ alias: 'ngpOverlayTrigger', required: true }) templateRef!: TemplateRef<void>;

  /**
   * Define if the trigger should be disabled.
   * @default false
   */
  @Input({ alias: 'ngpOverlayDisabled', transform: booleanAttribute }) disabled: boolean = false;

  /**
   * Define the placement of the overlay relative to the trigger.
   * @default 'bottom'
   */
  @Input('ngpOverlayPlacement') placement: Placement = 'top';

  /**
   * Define the offset of the overlay relative to the trigger.
   * @default 4
   */
  @Input({ alias: 'ngpOverlayOffset', transform: numberAttribute }) offset: number = 4;

  /**
   * Define the delay before the overlay is displayed.
   * @default 0
   */
  @Input({ alias: 'ngpOverlayShowDelay', transform: numberAttribute }) showDelay: number = 0;

  /**
   * Define the delay before the overlay is hidden.
   * @default 0
   */
  @Input({ alias: 'ngpOverlayHideDelay', transform: numberAttribute }) hideDelay: number = 0;

  /**
   * Define whether the overlay should shift when the overlay is near the edge of the viewport.
   * @default true
   */
  @Input({ alias: 'ngpOverlayShift', transform: booleanAttribute }) shift: boolean = true;

  /**
   * Define whether the overlay should flip when there is not enough space for the overlay.
   * @default true
   */
  @Input({ alias: 'ngpOverlayFlip', transform: booleanAttribute }) flip: boolean = true;

  /**
   * Define the container in to which the overlay should be attached.
   * @default document.body
   */
  @Input('ngpOverlayContainer') container: HTMLElement = document.body;

  /**
   * Store the overlay content instance.
   */
  private overlay: NgpOverlayDirective | null = null;

  /**
   * Store the overlay arrow instance.
   */
  private arrow: NgpOverlayArrowDirective | null = null;

  /**
   * Store the view ref
   */
  private viewRef: EmbeddedViewRef<void> | null = null;

  /**
   * Store the show delay timeout
   */
  private showDelayTimeout: number | null = null;

  /**
   * Store the hide delay timeout
   */
  private hideDelayTimeout: number | null = null;

  /**
   * Store the dispose function
   */
  private dispose?: () => void;

  /**
   * Store additional providers to register on the overlay.
   */
  private readonly providers: StaticProvider[] = [];

  /**
   * Determine the state of the overlay.
   */
  private get isOpen(): boolean {
    return !!this.viewRef;
  }

  /**
   * Determine the state of the overlay.
   */
  protected get state(): 'closed' | 'opening' | 'open' | 'closing' {
    if (this.showDelayTimeout) {
      return 'opening';
    }

    if (this.hideDelayTimeout) {
      return 'closing';
    }

    return this.isOpen ? 'open' : 'closed';
  }

  /**
   * Create the overlay.
   */
  private createOverlay(): void {
    const domPortal = new DomPortalOutlet(
      this.container,
      this.componentFactoryResolver,
      this.appRef,
      this.injector,
    );

    const templatePortal = new TemplatePortal(
      this.templateRef,
      this.viewContainer,
      undefined,
      Injector.create({
        parent: this.injector,
        providers: [
          {
            provide: NgpOverlayTriggerToken,
            useValue: this,
          },
          ...this.providers,
        ],
      }),
    );

    this.viewRef = domPortal.attach(templatePortal);
    this.viewRef.detectChanges();

    this.updateOverlayPosition();
    this.showDelayTimeout = null;
  }

  /**
   * Update the overlay position.
   */
  private updateOverlayPosition(): void {
    if (!this.viewRef) {
      return;
    }

    const overlayElement = this.viewRef.rootNodes[0] as HTMLElement;

    const middleware: Middleware[] = [];

    if (this.offset) {
      middleware.push(offset(this.offset));
    }

    if (this.shift) {
      middleware.push(shift());
    }

    if (this.flip) {
      middleware.push(flip());
    }

    // if there is an arrow defined, we need to add the arrow middleware
    if (this.arrow) {
      middleware.push(arrow({ element: this.arrow.elementRef.nativeElement }));
    }

    this.dispose = autoUpdate(this.trigger.nativeElement, overlayElement, async () => {
      const position = await computePosition(this.trigger.nativeElement, overlayElement, {
        placement: this.placement,
        middleware,
      });

      this.overlay?.setPosition(position.x, position.y);

      if (position.middlewareData.arrow) {
        this.arrow?.setPosition(
          this.placement,
          position.middlewareData.arrow.x,
          position.middlewareData.arrow.y,
        );
      }
    });
  }

  /**
   * Destroy the overlay.
   */
  private destroyOverlay(): void {
    this.viewRef?.destroy();
    this.viewRef = null;

    this.dispose?.();
    this.hideDelayTimeout = null;
  }

  /**
   * Show the overlay.
   */
  show(): void {
    if (this.disabled || this.isOpen) {
      return;
    }

    if (this.hideDelayTimeout) {
      clearTimeout(this.hideDelayTimeout);
      this.hideDelayTimeout = null;
    }

    this.showDelayTimeout = window.setTimeout(() => this.createOverlay(), this.showDelay);
  }

  /**
   * Hide the overlay.
   */
  hide(): void {
    if (!this.isOpen) {
      return;
    }

    if (this.showDelayTimeout) {
      clearTimeout(this.showDelayTimeout);
      this.showDelayTimeout = null;
    }

    this.hideDelayTimeout = window.setTimeout(() => this.destroyOverlay(), this.hideDelay);
  }

  /**
   * Register the overlay.
   * @param overlay The overlay to register.
   * @internal
   */
  registerOverlay(overlay: NgpOverlayDirective): void {
    this.overlay = overlay;
  }

  /**
   * Unregister the overlay.
   * @internal
   */
  unregisterOverlay(): void {
    this.overlay = null;
  }

  /**
   * Register the arrow.
   * @param arrow The arrow to register.
   * @internal
   */
  registerArrow(arrow: NgpOverlayArrowDirective): void {
    this.arrow = arrow;
  }

  /**
   * Unregister the arrow.
   * @internal
   */
  unregisterArrow(): void {
    this.arrow = null;
  }

  /**
   * Register a provider on the overlay.
   * @param provider The provider to register.
   * @internal
   */
  registerProvider(provider: StaticProvider): void {
    this.providers.push(provider);
  }
}

import { Directive, Input, numberAttribute } from '@angular/core';
import { NgpProgressToken } from './progress.token';

@Directive({
  selector: '[ngpProgress]',
  standalone: true,
  providers: [{ provide: NgpProgressToken, useExisting: NgpProgressDirective }],
  host: {
    role: 'progressbar',
    '[attr.aria-valuemax]': 'max',
    '[attr.aria-valuemin]': '0',
    '[attr.aria-valuenow]': 'value',
    '[attr.aria-valuetext]': 'valueLabel(value, max)',
    '[attr.data-state]': 'state',
    '[attr.data-value]': 'value',
    '[attr.data-max]': 'max',
  },
})
export class NgpProgressDirective {
  /**
   * Define the progress value.
   */
  @Input({ alias: 'ngpProgressValue', transform: numberAttribute }) value: number = 0;

  /**
   * Define the progress max value.
   * @default 100
   */
  @Input({ alias: 'ngpProgressMax', transform: numberAttribute }) max: number = 100;

  /**
   * Define a function that returns the progress value label.
   */
  @Input('ngpProgressValueLabel') valueLabel: (value: number, max: number) => string = (
    value,
    max,
  ) => `${Math.round((value / max) * 100)}%`;

  /**
   * Get the state of the progress bar.
   * @returns 'indeterminate' | 'loading' | 'complete'
   * @internal
   */
  get state(): 'indeterminate' | 'loading' | 'complete' {
    return this.value == null ? 'indeterminate' : this.value === this.max ? 'complete' : 'loading';
  }
}

import { Directive } from '@angular/core';
import { injectProgress } from '../progress/progress.token';

@Directive({
  selector: '[ngpProgressIndicator]',
