    '[attr.data-value]': 'progress.value',
    '[attr.data-max]': 'progress.max',
  },
})
export class NgpProgressIndicatorDirective {
  /**
   * Access the progress directive.
   */
  protected readonly progress = injectProgress();
}

import {
  Directive,
  EventEmitter,
  HostListener,
  Input,
  Output,
  booleanAttribute,
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { NgpRovingFocusGroupDirective } from '@ng-primitives/ng-primitives/roving-focus';
import { NgpRadioGroupToken } from './radio-group.token';

@Directive({
  selector: '[ngpRadioGroup]',
  standalone: true,
  providers: [
    { provide: NgpRadioGroupToken, useExisting: NgpRadioGroupDirective },
    { provide: NG_VALUE_ACCESSOR, useExisting: NgpRadioGroupDirective, multi: true },
  ],
  hostDirectives: [NgpRovingFocusGroupDirective],
  host: {
    role: 'radiogroup',
    '[attr.aria-orientation]': 'orientation',
    '[attr.data-disabled]': 'disabled ? "" : null',
  },
})
export class NgpRadioGroupDirective implements ControlValueAccessor {
  /**
   * The value of the radio group.
   */
  @Input('ngpRadioGroupValue') value?: string;

  /**
   * Whether the radio group is disabled.
   */
  @Input({ alias: 'ngpRadioGroupDisabled', transform: booleanAttribute }) disabled: boolean = false;

  /**
   * The orientation of the radio group.
   * @default 'horizontal'
   */
  @Input('ngpRadioGroupOrientation') orientation: 'horizontal' | 'vertical' = 'horizontal';

  /**
   * Event emitted when the value of the radio group changes.
   */
  @Output('ngpRadioGroupValueChange') readonly valueChange = new EventEmitter<string>();

  /**
   * The callback function to call when the value of the radio group changes.
   * @internal
   */
  private onChange?: (value: string) => void;

  /**
   * The callback function to call when the radio group is touched.
   * @internal
   */
  private onTouched?: () => void;

  /**
   * Select a radio item.
   * @param value The value of the radio item to select.
   */
  select(value: string): void {
    this.value = value;
    this.valueChange.emit(value);
    this.onChange?.(value);
  }

  /**
   * Update the value of the radio group.
   * @param value The new value of the radio group.
   * @internal
   */
  writeValue(value: string): void {
    this.value = value;
  }

  /**
   * Register a callback function to call when the value of the radio group changes.
   * @param fn The callback function to call when the value of the radio group changes.
   * @internal
   */
  registerOnChange(fn: (value: string) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  /**
   * Set the disabled state of the radio group.
   * @param isDisabled Whether the radio group is disabled.
   * @internal
   */
  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  /**
   * When focus leaves the radio group, mark it as touched.
   * @internal
   */
  @HostListener('focusout')
  protected onFocusout(): void {
    this.onTouched?.();
  }
}

import { Directive } from '@angular/core';
import { injectRadioGroup } from '../radio-group/radio-group.token';
import { injectRadioItem } from '../radio-item/radio-item.token';

@Directive({
  selector: '[ngpRadioIndicator]',
  standalone: true,
  host: {
    '[attr.data-state]': 'radioGroup.value === radioItem.value ? "checked" : "unchecked"',
    '[attr.data-disabled]': 'radioItem.disabled ? "" : null',
  },
})
export class NgpRadioIndicatorDirective {
  /**
   * Access the radio group.
   */
  protected readonly radioGroup = injectRadioGroup();

  /**
   * Access the radio group item.
   */
  protected readonly radioItem = injectRadioItem();
}

import { Directive, HostListener, Input, booleanAttribute } from '@angular/core';
import { NgpRovingFocusItemDirective } from '@ng-primitives/ng-primitives/roving-focus';
import { injectRadioGroup } from '../radio-group/radio-group.token';
import { NgpRadioItemToken } from './radio-item.token';

@Directive({
  selector: 'button[ngpRadioItem]',
  standalone: true,
  hostDirectives: [NgpRovingFocusItemDirective],
  providers: [{ provide: NgpRadioItemToken, useExisting: NgpRadioItemDirective }],
  host: {
    type: 'button',
    role: 'radio',
    '[attr.aria-checked]': 'radioGroup.value === value ? "true" : "false"',
    '[attr.data-disabled]': 'disabled ? "" : null',
    '[attr.data-state]': 'radioGroup.value === value ? "checked" : "unchecked"',
  },
})
export class NgpRadioItemDirective {
  /**
   * Access the radio group.
   */
  private readonly radioGroup = injectRadioGroup();

  /**
   * The value of the radio item.
   */
  @Input({ alias: 'ngpRadioItemValue', required: true }) value!: string;

  /**
   * Whether the radio item is disabled.
   * @default false
   */
  @Input({ alias: 'ngpRadioItemDisabled', transform: booleanAttribute }) disabled: boolean = false;

  /**
   * Handle keydown events.
   * @param event The keydown event.
   * @internal
   */
  @HostListener('keydown', ['$event'])
  protected onKeydown(event: KeyboardEvent): void {
    // According to WAI ARIA, radio groups don't activate items on enter keypress
    if (event.key === 'Enter') {
      event.preventDefault();
    }
  }

  /**
   * When the item receives focus, select it.
   * @internal
   */
  @HostListener('focus')
  protected onFocus(): void {
    this.radioGroup.select(this.value);
  }

  /**
   * When the item receives a click, select it.
   * @internal
   */
  @HostListener('click')
  protected onClick(): void {
    this.radioGroup.select(this.value);
  }
}

import {
  DestroyRef,
  Directive,
  ElementRef,
  EventEmitter,
  NgZone,
  OnInit,
  Output,
  inject,
} from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { ResizeEvent, fromResizeEvent } from '../utils/resize';

@Directive({
  selector: '[ngpResize]',
  standalone: true,
})
export class NgpResizeDirective implements OnInit {
  /**
   * Access the element.
   */
  private readonly element = inject<ElementRef<HTMLElement>>(ElementRef);

  /**
   * Access NgZone.
   */
  private readonly ngZone = inject(NgZone);

  /**
   * Access the destroy ref
   */
  private readonly destroyRef = inject(DestroyRef);

  /**
   * Emit when the element is resized.
   */
  @Output('ngpResize') readonly didResize = new EventEmitter<ResizeEvent>();

  ngOnInit(): void {
    // oberve the element for resize events (outside of Zone.js)
    this.ngZone.runOutsideAngular(() =>
      fromResizeEvent(this.element.nativeElement)
        .pipe(takeUntilDestroyed(this.destroyRef))
        .subscribe(event => this.didResize.emit(event)),
    );
  }
}

import { FocusKeyManager } from '@angular/cdk/a11y';
import { Directionality } from '@angular/cdk/bidi';
import {
  DestroyRef,
  Directive,
  Input,
  OnChanges,
  OnDestroy,
  OnInit,
  QueryList,
  SimpleChanges,
  booleanAttribute,
  inject,
} from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { filter } from 'rxjs';
import type { NgpRovingFocusItemDirective } from '../roving-focus-item/roving-focus-item.directive';
import { NgpRovingFocusGroupToken } from './roving-focus-group.token';

@Directive({
  selector: '[ngpRovingFocusGroup]',
  standalone: true,
  providers: [{ provide: NgpRovingFocusGroupToken, useExisting: NgpRovingFocusGroupDirective }],
})
export class NgpRovingFocusGroupDirective implements OnInit, OnChanges, OnDestroy {
  /**
   * Access the directionality service.
   */
  private readonly directionality = inject(Directionality);

  /**
   * Access the destroyRef
   */
  private readonly destroyRef = inject(DestroyRef);

  /**
   * Create a query list of all the roving focus items.
   * We don't use ContentChildren as dynamically added items may not be in the correct order.
   */
  private readonly items = new QueryList<NgpRovingFocusItemDirective>();

  /**
   * Create the focus key manager instance.
   * @internal
   */
  readonly keyManager = new FocusKeyManager<NgpRovingFocusItemDirective>(this.items);

  /**
   * Determine the orientation of the roving focus group.
   * @default vertical
   */
  @Input({ alias: 'ngpRovingFocusGroupOrientation' }) orientation: 'horizontal' | 'vertical' =
    'vertical';

  /**
   * Determine if focus should wrap when the end or beginning is reached.
   * @default true
   */
  @Input({ alias: 'ngpRovingFocusGroupWrap', transform: booleanAttribute }) wrap: boolean = true;

  ngOnInit(): void {
    this.keyManager.withWrap(this.wrap);

    // update the key manager orientation
    this.setOrientation(this.orientation);

    // update the key manager orientation if the document direction changes
    this.directionality.change
      .pipe(
        filter(() => this.orientation === 'horizontal'),
        takeUntilDestroyed(this.destroyRef),
      )
      .subscribe(direction => this.keyManager.withHorizontalOrientation(direction));
  }

  ngOnChanges(changes: SimpleChanges): void {
    // update the key manager orientation
    if ('orientation' in changes) {
      this.setOrientation(this.orientation);
    }

    // update the key manager wrap
    if ('wrap' in changes) {
      this.keyManager.withWrap(this.wrap);
    }
  }

  ngOnDestroy(): void {
    this.keyManager.destroy();
  }

  /**
   * Register a the roving focus item.
   * @param item The roving focus item to register.
   */
  register(item: NgpRovingFocusItemDirective): void {
    // add the item to the query list by sort the items based on their order
    this.items.reset([...this.items.toArray(), item].sort((a, b) => a.order - b.order));

    // if this is the first item, make it the active item
    if (this.items.length === 1) {
      this.keyManager.updateActiveItem(item);
    }
  }

  /**
   * Unregister a the roving focus item.
   * @param item The roving focus item to unregister.
   */
  unregister(item: NgpRovingFocusItemDirective): void {
    // determine if the item being removed is the active item
    const isActive = this.keyManager.activeItem === item;

    // remove the item from the query list
    this.items.reset(this.items.toArray().filter(i => i !== item));

    // if the item being removed is the active item, make the first item the active item
    if (isActive) {
      this.keyManager.updateActiveItem(0);
    }
  }

  /**
   * Handle key events on the roving focus items.
   * @param event The key event.
   * @internal
   */
  onKeydown(event: KeyboardEvent): void {
    this.keyManager.onKeydown(event);
  }

  /**
   * Set the orientation of the roving focus group.
   * @param orientation The orientation of the roving focus group.
   */
  setOrientation(orientation: 'horizontal' | 'vertical'): void {
    this.orientation = orientation;

    if (orientation === 'horizontal') {
      this.keyManager.withHorizontalOrientation(this.directionality.value);
    } else {
      this.keyManager.withVerticalOrientation();
    }
  }
}

import { FocusableOption } from '@angular/cdk/a11y';
import {
  ChangeDetectorRef,
  DestroyRef,
  Directive,
  ElementRef,
  HostBinding,
  HostListener,
  Input,
  OnDestroy,
  OnInit,
  booleanAttribute,
  inject,
  numberAttribute,
} from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { injectRovingFocusGroup } from '../roving-focus-group/roving-focus-group.token';
import { NgpRovingFocusItemToken } from './roving-focus-item.token';

@Directive({
  selector: '[ngpRovingFocusItem]',
  standalone: true,
  providers: [{ provide: NgpRovingFocusItemToken, useExisting: NgpRovingFocusItemDirective }],
})
export class NgpRovingFocusItemDirective implements OnInit, OnDestroy, FocusableOption {
  /**
   * Access the group the roving focus item belongs to.
   */
  private readonly group = injectRovingFocusGroup();

  /**
   * Access the element reference of the roving focus item.
   */
  private readonly elementRef = inject(ElementRef<HTMLElement>);

  /**
   * Access the destroyRef
   */
  private readonly destroyRef = inject(DestroyRef);

  /**
   * Access the change detector ref
   */
  private readonly changeDetectorRef = inject(ChangeDetectorRef);

  /**
   * Define the order of the roving focus item in the group.
   */
  @Input({ alias: 'ngpRovingFocusItemOrder', transform: numberAttribute }) order: number = 0;

  /**
