  /**
   * Access the connected overlay directive.
   * @internal
   */
  readonly overlayOrigin = inject(CdkOverlayOrigin);

  /**
   * Access the element reference.
   */
  private readonly elementRef = inject(ElementRef);

  /**
   * Access the destroy reference.
   */
